import { Component, React } from 'react';
import { Col, Row, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
class DisplaySearch extends Component {
constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {Content: '', MediaType: 'all', SearchQuery: '', Favourites: []};
    
}
    handleSubmit() {
        let SearchObject = {"MediaType": this.state.MediaType, "SearchQuery": this.state.SearchQuery};
        console.log("FETCHRUN");
        fetch("/Data", {
        method: "PUT",
        headers: {
            "Content-Type": "application/json"
        },
        body: JSON.stringify(SearchObject)})
        .then(res => res.json())
        .then(
            (result) => {
                this.setState({
                    Content: result
                });
            },
            (error) => {
                console.log(error);
                this.setState({
                    Content: 'error'
                });
            }
        )
        
    }


    render() {
        const COPYCONTENTS = this.state.Content.results;
        const COPYFAVS = this.state.Favourites;
        let resultCount = this.state.Content.resultCount;
        console.log("BODYRUN");
        if (this.state.Content === 'error') {
            return <div>Error 404</div>;
        } else if (this.state.Content === '') {
            return (
            <Container>
                <Row>
                    <Col>
                    <input onChange={(e) => this.setState({SearchQuery: e.target.value})}></input>
                    <button onClick={() => this.handleSubmit()}>Search</button>
                    <form>
                        <select onChange={(e) => {this.setState({MediaType: e.target.value})}}>
                            <option value="all">All</option>
                            <option value="movie">Movie</option>
                            <option value="podcast">Podcast</option>
                            <option value="music">Music</option>
                            <option value="musicVideo">Music Video</option>
                            <option value="audiobook">Audio Book</option>
                            <option value="shortFilm">Short Film</option>
                            <option value="tvShow">TV Show</option>
                            <option value="software">Software</option>
                            <option value="ebook">E-book</option>
                            <option value="all">All</option>
                        </select>
                    </form>
                    <div>
                        <h1>Favourites</h1>
                        {COPYFAVS.map((COPYFAV, index) => {
                            console.log(JSON.parse(COPYFAV))
                            return (
                                <img key={index} src={COPYFAVS} alt="favImg"/>
                            )
                        })}
                    </div>
                    </Col>
                </Row>
            </Container>
            )
        } else if (resultCount === 0) {
            return (
            <div>
                <h1>No results found</h1>
                <button onClick={() => this.setState({Content: '', MediaType: 'all', SearchQuery: ''})}>Back</button>
            </div>
            )
        } else
        return (
            <div>

                <Container>
                    <Row>
                        <Col>
                            <button onClick={() => this.setState({Content: '',MediaType: 'all',SearchQuery: ''})}>Back</button>
                        </Col>
                    </Row>
                    
                        {COPYCONTENTS.map((COPYCONTENT, index) =>{
                        return (
                            <Row>
                                <Col>
                                    <div key={index}>
                                    <img src={COPYCONTENT.artworkUrl100} width='100' alt="sourceImage"/>
                                    <h2>{COPYCONTENT.artistName}</h2>
                                    <h3>{COPYCONTENT.trackName}</h3>
                                    <h5>{COPYCONTENT.primaryGenreName}</h5>
                                    <p>{COPYCONTENT.shortDescription}</p>
                                    </div>
                                </Col>
                                <Col>
                                   <button onClick={() => {
                                       this.setState({ Favourites: [...this.state.Favourites, COPYCONTENT.artworkUrl100] })
                                       }}>Favorite</button>
                                </Col>
                                </Row>
                            )})}
                    
                </Container>


            </div>
        )


    }


}

export default DisplaySearch;
