import { Component, React } from 'react';
import { Col, Row, Container } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.min.css';
class DisplaySearch extends Component {
constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleMediaType = this.handleMediaType.bind(this);
    this.state = {Content: '', MediaType: 'movie', SearchQuery: ''};
    
}
    handleSubmit() {
        let SearchObject = Object.assign({"MediaType": this.state.MediaType, "SearchQuery": this.state.SearchQuery})
        console.log(SearchObject);
        fetch("/Data", {
        method: "POST",
        body: JSON.stringify(SearchObject)})
        .then(res => res.json())
        .then(
            (result) => {
                
                this.setState({
                    Content: result
                });
            },
            (error) => {
                console.log(error);
                this.setState({
                    Content: 'error'
                });
            }
        )
        
    }


    handleMediaType() {

    }

   

    render() {
        const COPYCONTENTS = this.state.Content.results;
        let resultCount = this.state.Content.resultCount;
        console.log(COPYCONTENTS);
        if (this.state.Content === 'error') {
            return <div>Error 404</div>;
        } else if (this.state.Content === '') {
            return (
            <Container>
                <Row>
                    <Col>
                    <input onChange={(e) => this.setState({SearchQuery: e.target.value})}></input>
                    <button onClick={() => this.handleSubmit()}>Search</button>
                    <button onClick={() => this.setState({MediaType: 'movie'})}>Movies</button>
                    </Col>
                </Row>
            </Container>
            )
        } else if (resultCount === 0) {
            return (
            <div>
                <h1>No results found</h1>
                <button onClick={() => this.setState({Content: ''})}>Back</button>
            </div>
            )
        } else
        return (
            <div>

                <Container>
                    <Row>
                        <Col>
                            <button onClick={() => this.setState({Content: ''})}>Back</button>
                        </Col>
                    </Row>
                    
                        {COPYCONTENTS.map((COPYCONTENT, index) =>{
                        return (
                            <Row>
                                <Col>
                                    <div key={index}>
                                    <img src={COPYCONTENT.artworkUrl100} width='100' alt="sourceImage"/>
                                    <h2>{COPYCONTENT.artistName}</h2>
                                    <h3>{COPYCONTENT.trackName}</h3>
                                    <h5>{COPYCONTENT.primaryGenreName}</h5>
                                    <p>{COPYCONTENT.shortDescription}</p>
                                    </div>
                                </Col>
                                <Col>
                                   <button onClick={() => window.location.href=`${COPYCONTENT.collectionViewUrl}`}>${COPYCONTENT.trackPrice}</button>
                                </Col>
                                </Row>
                            )})}
                    
                </Container>


            </div>
        )


    }


}

export default DisplaySearch;
