import { Component, React } from 'react';

class DisplaySearch extends Component {
constructor(props) {
    super(props);
    this.state = {Content: ''}
}
    componentDidMount() {
        fetch("https://itunes.apple.com/search?term=Minions&media=movie", {mode:'n0-cors'})
        .then(res => res.json())
        .then(
            (result) => {
                console.log(result);
                this.setState({
                    Content: result
                });
            },
            (error) => {
                console.log(error);
                this.setState({
                    Content: 'error'
                });
            }
        )
        
    }

    render() {

        return (
            <div>{this.state.Content}</div>
        )


    }


}

export default DisplaySearch;
